import React, { useEffect, useMemo, useState } from 'react';
import api from '../services/api';

const containerStyle = {
  border: '1px solid #7B68EE',
  borderRadius: 12,
  padding: 16,
  background: 'rgba(255,255,255,0.04)',
};

const WalletRow = ({ wallet, canRemove = false, onRemove = () => {} }) => {
  const handleCopy = async () => {
    if (!navigator || !navigator.clipboard || !navigator.clipboard.writeText) return;
    try {
      await navigator.clipboard.writeText(wallet.address);
    } catch (err) {
      console.warn('Copy failed', err);
    }
  };

  return (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        border: '1px solid rgba(123,104,238,0.6)',
        borderRadius: 8,
        padding: '8px 12px',
        background: 'rgba(255,255,255,0.05)',
      }}
    >
      <div>
        <div style={{ fontFamily: 'monospace', fontSize: 13 }}>{wallet.address}</div>
        <div style={{ fontSize: 12, opacity: 0.8 }}>
          {Number(wallet.balanceSOL || 0).toFixed(4)} SOL {wallet.type === 'source' ? '(deposit)' : '(bot)'}
        </div>
      </div>
      <div style={{ display: 'flex', gap: 8 }}>
        <button onClick={handleCopy}>Copy</button>
        {canRemove && (
          <button
            onClick={() => onRemove(wallet.address)}
            style={{ background: '#b34d6e', color: '#fff' }}
          >
            Remove
          </button>
        )}
      </div>
    </div>
  );
};

const WalletManager = ({ numWallets, refreshToken, onChanged }) => {
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [confirmText, setConfirmText] = useState('');
  const [error, setError] = useState('');

  const refresh = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await api.get('/wallets/list');
      setList(res.data.wallets || []);
    } catch (e) {
      console.error('Failed to load wallets', e);
      setError(e.response?.data?.error || 'Failed to fetch wallets');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    refresh();
  }, [refreshToken]);

  const depositWallet = useMemo(
    () => list.find((wallet) => wallet.type === 'source'),
    [list]
  );
  const botWallets = useMemo(
    () => list.filter((wallet) => wallet.type === 'sub'),
    [list]
  );
  const totalSubWallets = botWallets.length;
  const displayCount = numWallets ?? totalSubWallets;

  const addOne = async () => {
    try {
      await api.post('/wallets/add-one', {});
      await refresh();
      onChanged?.();
    } catch (e) {
      setError(e.response?.data?.error || 'Error adding wallet');
    }
  };

  const removeOne = async (address) => {
    if (confirmText !== 'confirm') {
      setError('Type "confirm" in the box below to enable removal.');
      return;
    }
    try {
      await api.post('/wallets/remove-one', { address, confirm: 'confirm' });
      setConfirmText('');
      await refresh();
      onChanged?.();
    } catch (e) {
      setError(e.response?.data?.error || 'Error removing wallet');
    }
  };

  return (
    <div style={containerStyle}>
      <h2 style={{ marginTop: 0 }}>Wallets</h2>
      <p style={{ marginTop: 0, marginBottom: 12 }}>
        Manage every wallet linked to your account. Use these for deposits and the trading bot.
      </p>

      <div
        style={{
          display: 'flex',
          gap: 12,
          flexWrap: 'wrap',
          fontSize: 13,
          marginBottom: 12,
        }}
      >
        <span>Total wallets: {list.length}</span>
        <span>Bot wallets: {totalSubWallets}</span>
        {typeof displayCount === 'number' && (
          <span>Bot wallets selected: {displayCount}</span>
        )}
      </div>

      {error && (
        <div style={{ color: 'salmon', fontSize: 13, marginBottom: 12 }}>{error}</div>
      )}

      <div style={{ display: 'grid', gap: 16 }}>
        <div>
          <h3 style={{ margin: '0 0 8px' }}>Deposit Wallet</h3>
          {loading ? (
            <em>Loading wallets...</em>
          ) : depositWallet ? (
            <WalletRow wallet={depositWallet} />
          ) : (
            <p style={{ fontSize: 13, margin: 0 }}>
              Deposit wallet not generated yet. Use the Deposit &amp; Withdraw panel to create one.
            </p>
          )}
        </div>

        <div>
          <div
            style={{
              display: 'flex',
              alignItems: 'flex-end',
              gap: 12,
              flexWrap: 'wrap',
              marginBottom: 12,
            }}
          >
            <div>
              <h3 style={{ margin: '0 0 4px' }}>Bot Wallets</h3>
              <div style={{ fontSize: 12, opacity: 0.8 }}>
                These wallets rotate activity across the volume bot.
              </div>
            </div>
            <div style={{ display: 'flex', gap: 8 }}>
              <button title="Add wallet" onClick={addOne} style={{ fontWeight: 700 }}>
                + Add wallet
              </button>
            </div>
          </div>

          <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap', marginBottom: 12 }}>
            <input
              type="text"
              value={confirmText}
              onChange={(e) => setConfirmText(e.target.value)}
              placeholder='type "confirm" to allow deletes'
              style={{ width: 220 }}
            />
            <span style={{ fontSize: 12, opacity: 0.7, alignSelf: 'center' }}>
              Required before removing a wallet.
            </span>
          </div>

          {loading ? (
            <em>Loading wallets...</em>
          ) : botWallets.length === 0 ? (
            <p style={{ fontSize: 13, margin: 0 }}>No bot wallets yet.</p>
          ) : (
            <div style={{ display: 'grid', gap: 8 }}>
              {botWallets.map((wallet) => (
                <WalletRow
                  key={wallet.address}
                  wallet={wallet}
                  canRemove
                  onRemove={removeOne}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WalletManager;
