import React, { useEffect, useMemo, useState } from 'react';
import api from '../services/api';

const BASE58_RE = /^[1-9A-HJ-NP-Za-km-z]{32,44}$/;

/** Pretty UI primitives **/
const Field = ({ label, children, hint, error }) => (
  <div style={{ display: 'grid', gap: 6 }}>
    <label style={{ fontSize: 12, opacity: 0.9 }}>{label}</label>
    {children}
    {hint && !error && <div style={{ fontSize: 12, opacity: 0.8 }}>{hint}</div>}
    {error && <div style={{ color: 'salmon', fontSize: 12 }}>{error}</div>}
  </div>
);

const Card = ({ children }) => (
  <div
    style={{
      background: 'rgba(255,192,203,0.12)',
      border: '1px solid rgba(255,192,203,0.35)',
      borderRadius: 12,
      padding: 16,
    }}
  >
    {children}
  </div>
);

const Input = (props) => (
  <input
    {...props}
    style={{
      padding: '8px 10px',
      border: '1px solid #9370DB',
      borderRadius: 8,
      background: 'rgba(255,255,255,0.9)',
      width: '100%',
      ...props.style,
    }}
  />
);

const Select = (props) => (
  <select
    {...props}
    style={{
      padding: '8px 10px',
      border: '1px solid #9370DB',
      borderRadius: 8,
      background: 'rgba(255,255,255,0.9)',
      width: '100%',
    }}
  />
);

const BotControls = ({ running }) => {
  const [tokenMint, setTokenMint] = useState('');
  const [rpc, setRpc] = useState('');
  const [minBuy, setMinBuy] = useState('');
  const [maxBuy, setMaxBuy] = useState('');
  const [minDelay, setMinDelay] = useState('');
  const [maxDelay, setMaxDelay] = useState('');
  const [mode, setMode] = useState('pure');

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [starting, setStarting] = useState(false);
  const [stopping, setStopping] = useState(false);
  const [errors, setErrors] = useState({});
  const [allWallets, setAllWallets] = useState([]);
  const [activeWallets, setActiveWallets] = useState([]);

  useEffect(() => {
    (async () => {
      try {
        const { data } = await api.get('/settings/get');
        setTokenMint(data.tokenMint || '');
        setRpc(data.rpc || '');
        setMinBuy(data.minBuy !== undefined ? String(data.minBuy) : '');
        setMaxBuy(data.maxBuy !== undefined ? String(data.maxBuy) : '');
        setMinDelay(data.minDelay !== undefined ? String(data.minDelay) : '');
        setMaxDelay(data.maxDelay !== undefined ? String(data.maxDelay) : '');
        setMode(data.mode || 'pure');
        setAllWallets(data.allWallets || []);
        setActiveWallets(data.activeWallets || []);
      } catch (e) {
        console.error('Failed to load settings', e);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  // Keep inputs as TEXT so users can type freely (prevents 1-char bug), then parse on save.
  const validation = useMemo(() => {
    const errs = {};
    if (!tokenMint || !BASE58_RE.test(tokenMint.trim())) errs.tokenMint = 'Enter a valid Solana mint address.';
    const nMinBuy = Number(minBuy);
    const nMaxBuy = Number(maxBuy);
    if (!(nMinBuy > 0)) errs.minBuy = 'Must be a number > 0.';
    if (!(nMaxBuy > 0)) errs.maxBuy = 'Must be a number > 0.';
    if (nMinBuy > 0 && nMaxBuy > 0 && nMinBuy > nMaxBuy) errs.maxBuy = 'Max must be ≥ Min.';
    const nMinDelay = Number(minDelay);
    const nMaxDelay = Number(maxDelay);
    if (!(nMinDelay >= 0)) errs.minDelay = 'Must be ≥ 0 ms.';
    if (!(nMaxDelay > 0)) errs.maxDelay = 'Must be > 0 ms.';
    if (Number.isFinite(nMinDelay) && Number.isFinite(nMaxDelay) && nMinDelay > nMaxDelay) errs.maxDelay = 'Max must be ≥ Min.';
    if (rpc && !/^https?:\/\/.+/i.test(rpc.trim())) errs.rpc = 'Must be a valid URL (or leave blank).';
    if (!activeWallets.length) errs.activeWallets = 'Select at least one wallet for the bot.';
    return { errs, isValid: Object.keys(errs).length === 0 };
  }, [tokenMint, rpc, minBuy, maxBuy, minDelay, maxDelay, activeWallets]);

  useEffect(() => setErrors(validation.errs), [validation]);

  const parseNum = (v) => {
    const n = Number(v);
    return Number.isFinite(n) ? n : 0;
    };

  const saveWalletSelection = async () => {
    try {
      await api.post('/wallets/active', { addresses: activeWallets });
    } catch (e) {
      console.error(e);
      alert('Error saving wallet selection');
    }
  };

  const handleSaveSettings = async () => {
    if (!validation.isValid) return alert('Fix the errors first.');
    setSaving(true);
    try {
      await api.post('/settings/update', {
        tokenMint: tokenMint.trim(),
        rpc: rpc.trim(),
        minBuy: parseNum(minBuy),
        maxBuy: parseNum(maxBuy),
        minDelay: parseNum(minDelay),
        maxDelay: parseNum(maxDelay),
        mode,
      });
      await saveWalletSelection();
      alert('Settings saved');
    } catch (e) {
      console.error(e);
      alert(e.response?.data?.error || 'Error saving settings');
    } finally {
      setSaving(false);
    }
  };

  const handleStart = async () => {
    if (!validation.isValid) return alert('Fix the errors first.');
    setStarting(true);
    try {
      await api.post('/bot/start');
      alert('Bot started');
      // no forced reload → avoids losing input focus
    } catch (e) {
      console.error(e);
      alert(e.response?.data?.error || 'Error starting bot');
    } finally {
      setStarting(false);
    }
  };

  const handleStop = async () => {
    setStopping(true);
    try {
      await api.post('/bot/stop');
      alert('Bot stopped');
    } catch (e) {
      console.error(e);
      alert(e.response?.data?.error || 'Error stopping bot');
    } finally {
      setStopping(false);
    }
  };

  const toggleWallet = (addr) => {
    setActiveWallets((prev) => (prev.includes(addr) ? prev.filter((a) => a !== addr) : prev.concat(addr)));
  };

  if (loading) return <div style={{ margin: '20px 0' }}><em>Loading bot settings…</em></div>;

  const usingServerRpc = rpc.trim() === '';

  return (
    <div style={{ margin: '20px 0' }}>
      <h2 style={{ marginBottom: 12 }}>Volume Panel</h2>

      <div style={{ display: 'grid', gap: 12 }}>
        <Card>
          <div style={{ display: 'grid', gap: 12, gridTemplateColumns: '1fr 1fr' }}>
            <Field label="Token Mint Address" error={errors.tokenMint}>
              <Input
                type="text"
                value={tokenMint}
                onChange={(e) => setTokenMint(e.target.value)}
                placeholder="So1111... (token mint)"
              />
            </Field>

            <Field label="RPC URL" hint={usingServerRpc ? 'Using server mainnet RPC (default).' : undefined} error={errors.rpc}>
              <Input
                type="text"
                value={rpc}
                onChange={(e) => setRpc(e.target.value)}
                placeholder="Leave blank to use server mainnet RPC"
              />
            </Field>

            <Field label="Min Buy (SOL)" error={errors.minBuy}>
              <Input
                type="text"
                value={minBuy}
                onChange={(e) => setMinBuy(e.target.value)}
                placeholder="e.g., 0.05"
              />
            </Field>
            <Field label="Max Buy (SOL)" error={errors.maxBuy}>
              <Input
                type="text"
                value={maxBuy}
                onChange={(e) => setMaxBuy(e.target.value)}
                placeholder="e.g., 0.15"
              />
            </Field>

            <Field label="Min Delay (ms)" error={errors.minDelay}>
              <Input
                type="text"
                value={minDelay}
                onChange={(e) => setMinDelay(e.target.value)}
                placeholder="e.g., 4000"
              />
            </Field>
            <Field label="Max Delay (ms)" error={errors.maxDelay}>
              <Input
                type="text"
                value={maxDelay}
                onChange={(e) => setMaxDelay(e.target.value)}
                placeholder="e.g., 9000"
              />
            </Field>

            <Field label="Mode">
              <Select value={mode} onChange={(e) => setMode(e.target.value)}>
                <option value="pure">Pure</option>
                <option value="growth">Growth</option>
                <option value="moonshot">Moonshot</option>
                <option value="human">Human</option>
                <option value="bump">Bump</option>
              </Select>
            </Field>
          </div>

          <div style={{ marginTop: 12 }}>
            <button onClick={handleSaveSettings} disabled={saving}>
              {saving ? 'Saving…' : 'Save Settings'}
            </button>
            <button
              onClick={handleStart}
              disabled={running || starting || !validation.isValid}
              title={!validation.isValid ? 'Fix settings errors first' : undefined}
              style={{ marginLeft: 8 }}
            >
              {starting ? 'Starting…' : 'Start Bot'}
            </button>
            <button onClick={handleStop} disabled={!running || stopping} style={{ marginLeft: 8 }}>
              {stopping ? 'Stopping…' : 'Stop Bot'}
            </button>
          </div>
        </Card>

        <Card>
          <h3 style={{ marginTop: 0 }}>Pick Wallets for the Bot</h3>
          {(!allWallets || allWallets.length === 0) ? (
            <p>No wallets yet. Go to Wallets tab to generate some.</p>
          ) : (
            <div style={{ display: 'grid', gridTemplateColumns: '1fr', gap: 6 }}>
              {allWallets.map((addr) => (
                <label key={addr} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <input
                    type="checkbox"
                    checked={activeWallets.includes(addr)}
                    onChange={() => toggleWallet(addr)}
                  />
                  <span style={{ fontFamily: 'monospace', fontSize: 13 }}>{addr}</span>
                </label>
              ))}
            </div>
          )}
          {errors.activeWallets && <div style={{ color: 'salmon', fontSize: 12, marginTop: 4 }}>{errors.activeWallets}</div>}
        </Card>
      </div>
    </div>
  );
};

export default BotControls;
