import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Verify from './components/Verify';
import Signup from './components/Signup';
import Dashboard from './components/Dashboard';
import api from './services/api';
import './styles/App.css';

const App = () => {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState(null); // Track email for verification

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      api.get('/dashboard')
        .then((response) => setUser(response.data))
        .catch(() => localStorage.removeItem('token'));
    }
  }, []);

  return (
    <Router>
      <div className="App" style={{ backgroundColor: '#4B0082', minHeight: '100vh', color: '#9370DB' }}>
        <Routes>
          <Route
            path="/login"
            element={
              user ? (
                <Navigate to="/dashboard" />
              ) : (
                <Login setUser={setUser} setEmail={setEmail} /> // Pass setEmail to capture email
              )
            }
          />
          <Route
            path="/verify"
            element={
              user ? (
                <Navigate to="/dashboard" />
              ) : (
                <Verify setUser={setUser} email={email} /> // Pass email to Verify
              )
            }
          />
          <Route
            path="/signup"
            element={
              user ? (
                <Navigate to="/dashboard" />
              ) : (
                <Signup setUser={setUser} />
              )
            }
          />
          <Route
            path="/dashboard"
            element={user ? <Dashboard user={user} setUser={setUser} /> : <Navigate to="/login" />}
          />
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
