import React, { useState } from 'react';
import api from '../services/api';

const WalletManager = ({ numWallets, onChanged }) => {
  const [addCount, setAddCount] = useState('');
  const [removeCount, setRemoveCount] = useState('');
  const [c1, setC1] = useState('');
  const [c2, setC2] = useState('');

  const add = async () => {
    try {
      const count = Number(addCount);
      if (!Number.isInteger(count) || count <= 0) return alert('Enter a positive integer');
      await api.post('/wallets/manage', { action: 'add', count });
      alert('Wallets added');
      setAddCount('');
      onChanged?.();
    } catch (e) { alert('Error adding wallets'); }
  };

  const remove = async () => {
    try {
      const count = Number(removeCount);
      if (!Number.isInteger(count) || count <= 0) return alert('Enter a positive integer');
      await api.post('/wallets/manage', { action: 'remove', count, confirm1: c1, confirm2: c2 });
      alert('Wallets removed');
      setRemoveCount(''); setC1(''); setC2('');
      onChanged?.();
    } catch (e) { alert('Error removing wallets'); }
  };

  return (
    <div style={{ margin: '16px 0', border:'1px solid #7B68EE', borderRadius:8, padding:12 }}>
      <h2>Wallet Management</h2>
      <p>Current Sub-Wallets: {numWallets}</p>

      <div style={{ display:'flex', gap:8, alignItems:'center', flexWrap:'wrap' }}>
        <label>Add Wallets:</label>
        <input type="number" value={addCount} onChange={(e) => setAddCount(e.target.value)} placeholder="Number to add" />
        <button onClick={add}>Add</button>
      </div>

      <div style={{ display:'flex', gap:8, alignItems:'center', flexWrap:'wrap', marginTop:8 }}>
        <label>Remove Wallets:</label>
        <input type="number" value={removeCount} onChange={(e) => setRemoveCount(e.target.value)} placeholder="Number to remove" />
        <input type="text" value={c1} onChange={(e) => setC1(e.target.value)} placeholder="Type 'confirm'" />
        <input type="text" value={c2} onChange={(e) => setC2(e.target.value)} placeholder="Type 'confirm' again" />
        <button onClick={remove}>Remove</button>
      </div>
    </div>
  );
};

export default WalletManager;
